import React from 'react';
import './sign-up.css';
import content from '../../statistic/index.js';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers';
import * as yup from 'yup';

const SignUp = () => {
    const { register, handleSubmit, errors } = useForm();
    return ( 
    <div className='sign-up'>
        <h1>Don't an have account?</h1>
        <h3>Sign up here</h3>
        <form>
            {content.inputs.map((input,key) => {
                return (
                    <div key={key}>
                        <label htmlFor={input.name}>{input.label}</label>
                        <br/>
                        <input type={input.type} name={input.name} ref={register} />
                        <span className='message'>{errors[input.name]?.message}</span>
                    </div>
                )
            }
            )}

        {/*<label for="text">Full name</label>
            <input name='text' type='text' />
            <label for="email">Email</label>
            <input name='email' type='email' />
            <label for="password">Password</label>
            <input name='password' type='password' />*/}

        <label for="options">User Type</label>
        <select id="options">
            <option value="Developer">Developer</option>
            <option value="Owner">Owner</option>
        </select>
        <button>SIGN UP</button>

        </form>
        
        </div>
    )
}
export default SignUp;
