import React from 'react';
import './sign-up.css';
import content from './../static/index';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
 import * as yup from 'yup';
const schema = yup.object().shape(
    {
        fullname: yup.string().required().min(6),
        email: yup.string().required("please enter a valid email address"),
        password: yup.string().required("please enter password").matches(/^(?=.*[A-Za-z])(?=.*\d)(?.*[@$!%*#?&)[A-Za-z\d@$!%*#?&]{8,}$/,
        "Must Contain 8 Characters, One Uppercase, One Lowercase, One Number and one special case Character")
    }
)
const SignUp = () => {
    const { register, handleSubmit, errors } = useForm()(
        {
            resolver: yupResolver(schema)
        }
    )
    const onSubmit = (data) => console.log(data)

    return ( 
    <div className='sign-up'>
        <h1>Don't an have account?</h1>
        <h3>Sign up here</h3>
        <form onSubmit ={handleSubmit(onSubmit)}>
            {content.inputs.map((input,key) => {
                return (
                    <div key={key}>
                        <label htmlFor={input.name}>{input.label}</label>
                        <br/>
                        <input type={input.type} name={input.name} ref={register(input.name)} />
                         {/* <span className='message'>{errors[input.name]?.message}</span>  */}
                    </div>
                )
            }
            )}

        {/*<label for="text">Full name</label>
            <input name='text' type='text' />
            <label for="email">Email</label>
            <input name='email' type='email' />
            <label for="password">Password</label>
            <input name='password' type='password' />*/}

        <label for="options">User Type</label>
        <select id="options">
            <option value="Developer">Developer</option>
            <option value="Owner">Owner</option>
        </select>
        <button>SIGN UP</button>

        </form>
        
        </div>
    )
}
export default SignUp;
